CC++PythonJavaGo <pre><code>#include &lt;stdio.h&gt;

int main() {
    int n, num, xor_sum = 0, min_val = 1000001;
    long long int sum = 0;

    scanf("%d", &amp;n);
    for (int i = 0; i &lt; n; ++i) {
        scanf("%d", &amp;num);
        xor_sum ^= num;
        sum += num;
        if (num &lt; min_val) {
            min_val = num;
        }
    }

    if (xor_sum == 0) {
        printf("YES\n%lld\n", sum - min_val);
    } else {
        printf("NO\n");
    }

    return 0;
}
