#include &lt;algorithm&gt;

int main() {
    int n, num, xor_sum = 0, min_val = 1000001;
    long long sum = 0;

    std::cin &gt;&gt; n;
    for (int i = 0; i &lt; n; ++i) {
        std::cin &gt;&gt; num;
        xor_sum ^= num;
        sum += num;
        min_val = std::min(min_val, num);
    }

    if (xor_sum == 0) {
        std::cout &lt;&lt; "YES\n" &lt;&lt; sum - min_val &lt;&lt; std::endl;
    } else {
        std::cout &lt;&lt; "NO" &lt;&lt; std::endl;
    }

    return 0;
}
